import * as actionTypes from "../constants/cartConstants";

const CART_INITIAL_STATE = {
  cartItems: [],
  };

export const cartReducer = (state = CART_INITIAL_STATE, action) => {
  switch (action.type) {
    case actionTypes.ADD_TO_CART:
      const item = action.payload;
      console.log('In the reducer', item)
      const existItem = state.cartItems.find((x) => x.title === item.title)

      console.log("to check", existItem)
      if (existItem) {
        return {
          ...state,
          cartItems: state.cartItems.map((x) =>
            x.product === existItem.product ? item : x
          ),
        };
      } else {
        return {
          ...state,
          cartItems: [...state.cartItems, item],
        };
      }

    case actionTypes.ADD_TO_SEARCH:
      console.log('data in the reducer search',action.payload)
      const items = action.payload;
      console.log('In the reducer', items)
      const existItems = state.cartItems.find((x) => x.title === items.title)

      console.log("to check", existItems)
      if (existItems) {
        return {
          ...state,
          cartItems: state.cartItems.push(items),
        };
      } else {
        return {
          ...state,
          cartItems: [...state.cartItems, items],
        };
      }


    case actionTypes.REMOVE_FROM_CART:
      return {
        ...state,
        cartItems: state.cartItems.filter((x) => x.id !== action.payload),
      };
    default:
      return state;
  }
};
