import React from "react"
import { useSelector } from "react-redux"
import { useHistory } from "react-router-dom"
const CartScreen = () => {
    const history = useHistory()
    const products = useSelector((state) => state.cart.cartItems)
    const renderList = products.map((product) => {
        const { id, title, desc, img, price } = product
        console.log("data in the cartScreen", products)
        return (
            <div key={id} style={{ textAlign: "center",display:"flex"}}>
                <img src={img} alt={title} />
                <h5>{title}</h5>
                <h5>{desc}</h5>
                <h5>Price:Rs.{price}.00</h5>
                {/* <h4>
                    Total price=Rs.{price}.00
                </h4> */}


                {/* <button className="btn btn-warning mx-5"
                onClick={()=>{}}>Remove from cart</button> */}
            </div>
        );
    })
    return (
        <section className="text-center">
            <h3>Cart Items</h3>

            {renderList}


            <button onClick={() => {
                alert('Order placed successfully')
                history.push('/')
            }} className="btn btn-success m-5">Proceed to checkout</button>

        </section>

    )
}

export default CartScreen
