import axios from 'axios'
import React, { memo, useState } from 'react'
import { useSelector } from 'react-redux'
import { useHistory } from 'react-router-dom'
import "./view.css"


const Navbar = (props) => {
    const history = useHistory()
    const products = useSelector((state) => state.cart.cartItems)

    console.log("in the navbar", products.length)


    const username = localStorage.getItem('user')
    console.log(typeof username)
    const viewCart = () => {
        history.push('/cart')
    }


    const logout = () => {
        localStorage.removeItem('user')
    }


    const [user, setUser] = useState({
        title:""
    })

    // setUser(e.target.value)
    const handleChange = e => {
        const { name, value } = e.target
        setUser({
            ...user,
            [name]: value
        })
    }

    const dataSend=user.title
    const [data,setData]=useState()


    const searchBook=async()=>{
        const dataFromdb=await axios.get(`http://localhost:1337/search/${dataSend}`)
        console.log('here in the searched option',dataFromdb.data)
        setData(dataFromdb.data)
        console.log('drftvgbhnjmk',data)

        props.dataFromdb.data
    }

    console.log('here in the searched option2',data)

    return (
        <div>
            <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
                <a class="navbar-brand" >Library App</a>
                <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span class="navbar-toggler-icon"></span>
                </button>

                <div class="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul class="navbar-nav mr-auto">
                        <h4 className='text-white'>Welcome {username}  <i class="far fa-smile text-white"></i></h4>
                    </ul>

                    <div class="d-flex">
                        <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search"
                            name="title" value={user.title} onChange={handleChange}
                        ></input>
                        <button class ="btn btn-outline-success mx-2" 
                            onClick={searchBook}>Search</button>
                    </div>

                    <div class="form-inline my-2 my-lg-0">
                        <button onClick={viewCart} className="btn btn_cart">
                            <i class="fas fa-shopping-cart text-white h3 mx-5"><span className='cart_number' >{products.length}</span></i>
                        </button>

                        <button class="btn btn-warning my-2 mx-2 my-sm-0" onClick={() => { history.push('./Register') }}>Regitser</button>
                        {!username ? (
                            <button class="btn btn-success my-2 my-sm-0" onClick={() => { history.push('./Login') }}>Login</button>
                        ) : (
                            <button class="btn btn-danger my-2 my-sm-0" onClick={logout}>Logout</button>
                        )}
                    </div>
                </div>
            </nav>
        </div>
    )
}

export default memo(Navbar)
