            <div key={id} style={{ textAlign: "center", display: "flex", marginTop: "1rem" }}>
                <img src={img} alt={title} />
                <div className="ml-5">
                    <h5>{title}</h5>
                    <h5>{desc}</h5><br />
                    <h5>Price:Rs.{price}.00</h5>

                    {/* setMrp({price}) */}


