import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { useHistory } from 'react-router-dom'
import './view.css'
require('dotenv').config();

const View = () => {
    const history = useHistory()
    const [user, setUser] = useState()

    const [userdata, setUserdata] = useState()
    console.log('hello edflkemf', typeof user)
    useEffect(() => {

        setUser(localStorage.getItem('userID'))
        axios.get('http://localhost:1337/item')
            .then((response) => {
                console.log('user id from ls', localStorage.getItem('userID'))
                console.log("response", response)
                if (localStorage.getItem('userID') === response.data[0].userId.id) {
                    setUserdata(response.data)
                }
            }, (err) => {
                console.log(err)
            })
    }, [])

    let obj=userdata.find(userId=>userId.id===localStorage.getItem('userID'))

    // const useridFromDB=userdata.userId.id
    console.log("object is here ", userdata[0].userId.id)
    console.log("kkjbkb", obj)
    // console.log(books.data)
    return (
        <div className="itemsOfUser">

                {user ? (
                    userdata?.map((book, id) => (
                        <center>

                        <div class="card" key={id}>
                            <img src={book.img} class="card-img-top" alt="..." />
                            <div class="card-body">
                                <h5 class="card-title">{book.title}</h5>
                                <p class="card-text">{book.desc}</p>
                                <p class="card-text">Rs.{book.price}.00</p>
                                <a href="#" class="btn btn-primary">Buy Now</a>
                            </div>
                        </div>
                        </center>

            ))
                // <div>data</div>





            ) : (
            <center className="m-5">
                <h3>Sorry...
                    No user logged in yet.
                </h3>

                <h4>Please <a className="text-success" onClick={() => { history.push('/login') }}>click here</a> to login</h4>
            </center>
            )
            }
        </div>
    )
}

export default View
